html:
{% if letter_head and not no_letterhead -%}
    <div class="letter-head">{{ letter_head }}</div>
<hr>    
{%- endif %}
<h2><div class="text-center"> {{ "FACTURA" }} </div></h2>
<h4><div class="text-left">N. {{doc.name }} </div></h4>

<div class="row">
  <div class="col-xs-8" ><h4><b>Cliente: {{ doc.customer_name}}</b></h4>  
	<div class="row">
		{% if doc.project -%}		
			<div class="col-xs-6" ><b>Folha de Obra: </b>{{ doc.project}}</div>
		{%- endif %}	
        <div class="col-xs-6" ><b>Estado: </b>{{ doc.status }}</div>
    </div>

  </div>
  <div class="col-xs-4" ><b>Data:</b> {{ doc.get_formatted("posting_date") }} </div>

</div>


<p><br></p>
{% if doc.project -%}
{% set carro = frappe.get_doc("Folha de Obras", doc.project) %}

<div class="row">
  <div class="col-xs-8" >
		<style>
		table#t01 {
			font-family: arial, sans-serif;
			border-collapse: collapse;
			width: 70%;
		}

		td, th {
			border: 1px solid #dddddd;
			text-align: center;
			padding: 8px;
		}
		td#td01 {
			border: 1px solid #dddddd;
			text-align: right;
			padding: 8px;
		}


		tr:nth-child(even) {
			background-color: #dddddd;
		}

		</style>

		<table id="t01">
		  <tr>
			<td ><b>Marca/Modelo</b></td>
			<td> {{ carro.marca_veiculo}} </td>
		  </tr>
		  <tr>
			<td><b>Chassi</b></td>
			<td>{{ carro.numero_chassi}} </td>
		  </tr>
		  <tr>
			<td><b>Matricula</b></td>
			<td>{{ carro.matricula_veiculo}}</td>
		  </tr>
		</table>
	</div>
</div>
{% endif -%}

{% if doc.items%}
	<table class="table table-condensed cart " id="t02">
		<tbody>
			<tr>
				<th>Nr</th>
				<th style="text-center">Descrição</th>
				<th style="text-center">Qtd</th>
				<th style="text-center">Preço</th>
				<th style="text-center">Total</th>				
			</tr>
			{%- for row in doc.items -%}
			<tr>
				<td style="width: 2%;">{{ row.idx }}</td>
				<td style="text-center; width: 20%;">{{ row.item_code }} </td>
				<td style="text-center; width: 2%;">{{ row.qty }}</td>				
				<td id="td01" style="text-right; width: 8%;">{{ row.get_formatted("rate", doc)}}</td>
				<td id="td01" style="text-right; width: 8%;">{{ row.get_formatted("amount", doc)}}</td>

			</tr>

			{%- endfor -%}	
		</tbody>	
	</table>

{% endif %}

<div class="row">
  <div class="col-xs-10 text-right" ><h5>TOTAL KZ:</h5>    </div>
  <div class="col-xs-2 text-right" ><h5>{{ doc.get_formatted("total") }}</h5>  </div>
  {% set ipc = frappe.get_doc("Retencoes", "IPC") %}
  <div class="col-xs-10 text-right" ><h5><b>IPC {{ ipc.get_formatted("percentagem") | replace(".0","") }}</b></h5>    </div>
  <div class="col-xs-2 text-right" ><h5> {{ doc.get_formatted("total_taxes_and_charges") }}</h5>  </div>

  <div class="col-xs-10 text-right" ><h4><b>TOTAL GERAL KZ:</b></h4>    </div>
  <div class="col-xs-2 text-right" ><h5><b> </b> {{ doc.get_formatted("base_rounded_total") }}</h5>  </div>
 </div>
<div class="row">
	{% if doc.contra_valor -%}
  <div class="col-xs-10 text-right" ><h6><b>Contra Valor Usd: </b></h6>  </div>
  <div class="col-xs-2 text-right" ><h6><b> </b> {{ doc.get_formatted("contra_valor") }}</h6> </div>  
  {% endif -%}
 </div>

{% if doc.terms -%} 
	<h5><div class="text-left small">OBS: {{doc.terms}} </div></h5>
{% endif -%}	

<div class="row">
  <div class="col-xs-8" >
		<style>
		table#t03 {
			font-family: arial, sans-serif;
			border-collapse: collapse;
			width: 30%;
		}

		td, th {
			border: 1px solid #dddddd;
			text-align: center;
			padding: 8px;
		}
		tr:nth-child(even) {
			background-color: #dddddd;
		}

		</style>

		<table id="t03"> 
			<caption><b> Retenção na Fonte </b></caption>
		  <tr> 
			<td ><b>Base</b></td>
			<td ><b>Taxa</b></td>
			<td ><b>Valor</b></td>

		  </tr>
  		  <tr>
			<td >{{  doc.get_formatted("base_retencao_fonte")}}</td>
			<td >6,5%</td>
			<td> {{  doc.get_formatted("total_retencao_na_fonte")}} </td>
		  </tr>

		</table>
	</div>
</div>

<div id="footer-html" class="text-center small visible-pdf">
        {% if letter_head and not no_letterhead -%}
	{% set ff = frappe.get_doc("Letter Head", doc.letter_head) %}
        <div class="text-center small letter-head-footer">
            {{ ff.footer }}
        </div>
        {% endif %}
        <p class="text-center small page-number visible-pdf">
            {{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
        </p>
    </div>
